import { useEffect, useState } from "react";
import { Col, Container, Pagination, Row } from "react-bootstrap";
pointsu
import appServices from "../../services/appServices";
import CardLg from "../page-component/cardLg/CardLg";
import LeftSidebar from "./leftSidebar";



export default function MainComponent({ location, params }) {
    // const params = useParams();
    const [data, setData] = useState();
    const [active, setActive] = useState(1);
    const [total, setTotal] = useState(1);
    useEffect(() => {
        var t = {
            "categoryId": params?.id,
            "pageSize": 5,
            "currentPage": active,
            "search": "",
            "sortField": "string",
            "sortOrder": "string",
            "ids": [
              0
            ],
            "id": 0
          }
        appServices.postRequests({ ...t } , pointsU.sgetCardContent).then((res) => {
            setData(res.data?.data);
            setTotal(res.data?.total);
        });
    }, [params , active]);
    const PaginationBasic = () => {
        let items = [];
        for (let number = 1; number <= total; number++) {
            if (number > 10) return items;
            items.push(
                <Pagination.Item onClick={paginationChage} key={number} active={number === parseInt(active)}>
                    {number}
                </Pagination.Item>,
            );
        }

        return items;
    }
    const paginationChage = (e) => {
        var key = e.target.innerText;
        setActive(key);
    }

    // useEffect(() => {
    //     getIcerikler();
    // }, [active])

    return (
        <Container className="mt-100">
            <Row>
                {/* <RightSidebar/> */}
                <Col sm={8}>
                    <h1 class="heading_1"><span>{params?.category ?? ""}</span></h1>
                    <CardLg data={data} />
                    <Pagination onChange={paginationChage}>
                        <Pagination.First onClick={(e)=>{setActive(1)}}/>
                        <Pagination.Prev onClick={(e)=>{setActive(active-1)}}/>
                        <PaginationBasic />
                        <Pagination.Next onClick={(e)=>{setActive(active+1)}}/>
                        <Pagination.Last onClick={(e)=>{setActive(total)}}/>
                    </Pagination>
                </Col>
                <LeftSidebar />
            </Row>
        </Container>
    )
}
