import { Container, Row, Col } from "react-bootstrap";



export default function Topbar({ params , data }) {

    const DigerCards = () => {
        return (
            <>
                <Col xs={12} lg={6} className='right-card'>
                    <Row>
                        {
                        }
                    </Row>
                </Col>

                {
                    data?.map((b, i) => {
                        if (i === 0) {
                            return (
                                <Col key={i} xs={12} lg={6} className='left-card'>
                                    <div className="example-2 card">
                                        <div className="wrapper" style={{ background: `url(${"data:image/jpeg;base64," + b?.photo64}) center / cover no-repeat ` }}>
                                            <div className="header">
                                                <div className="date">
                                                    <span className="day">12 </span>
                                                    <span className="month">Aug </span>
                                                    <span className="year">2016 </span>
                                                </div>
                                                <ul className="menu-content">
                                                    <li>
                                                        <a href="#" className="fa fa-bookmark-o"></a>
                                                    </li>
                                                    <li><a href="#" className="fa fa-heart-o"><span>قراءة {b?.views ?? 0}  </span></a></li>
                                                </ul>
                                            </div>
                                            <div className="data">
                                                <div className="content">
                                                    <span className="author">akhbar Yemen</span>
                                                    <h1 className="title"><a href="#">{b?.title}</a></h1>
                                                    <p className="text">{b?.description}</p>
                                                    <NavLink to={"news/" + b?.id}>
                                                        <a href="#" className="button">تابع القراءة</a>
                                                    </NavLink>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </Col>
                            )
                        };
                    }
                    )
                }
            </>)
    }


    // if(!data.length) return "";
    return (
        <>
            <Container>
                <Row>
                    <DigerCards />
                </Row>
            </Container>
        </>
    )
}
