import { useEffect, useState } from "react";
import { Col, Row } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { useRouter } from "next/router";
import MediaEnums from "../enums/MediaEnums";
import pointsU from "points";
import { FaCalendar } from "react-icons/fa";
import { setSosyalAction } from "redux/app/actions";
import { postData } from "utils/fetchData";

export default function LeftSidebar() {

    const [sosyal, setSosyal] = useState();
    const [enFazlaOkuyanlar, setEnFazlaOkuyanlar] = useState();
    const [rssEvent, setRssEvent] = useState();
    const dispatch = useDispatch();
    const params = useRouter();
    const adsStore = useSelector(state => state.App?.ads?.leftSidebar ?? []);
    const [count, setCount] = useState(0);
    const bb = adsStore?.length;

    useEffect(() => {
        const getData = async () => {
            console.info("params");
            console.info(params);
            await postData(pointsU.getRssEvents,{
                "categoryId": parseInt(params?.id) ?? 0,
                "pageSize": 5,
                "currentPage": 1,
                "search": "",
                "sortField": "id",
                "sortOrder": "asc",
                "ids": [
                    0
                ],
                "id": 0
            }).then((res) => {
                setRssEvent(res.data?.data);
            });

        }
        const getData2 = async () => {
            console.info("params");
            console.info(params);
            await postData(pointsU.getRssEvents,{
                "categoryId": parseInt(params?.id) ?? 0,
                "pageSize": 5,
                "currentPage": 1,
                "search": "",
                "sortField": "id",
                "sortOrder": "asc",
                "ids": [
                    0
                ],
                "id": 0
            }).then((res) => {
                setRssEvent(res.data?.data);
            });
            await getData(pointsU.getSosyal).then((res) => {
                setSosyal(res.data);
                dispatch(setSosyalAction(res.data))
            });
            await postData(pointsU.GetMostReadedEvents ,{
                "categoryId": parseInt(params?.id) ?? 0,
                "pageSize": 5,
                "currentPage": 1,
                "search": "",
                "sortField": "id",
                "sortOrder": "asc",
                "ids": [
                    0
                ],
                "id": 0
            }).then((res) => {
                setEnFazlaOkuyanlar(res.data?.data);
            });

        }
        const getData3 = async () => {
            console.info("params");
            console.info(params);
            await postData(pointsU.getRssEvents,{
                "categoryId": parseInt(params?.id) ?? 0,
                "pageSize": 5,
                "currentPage": 1,
                "search": "",
                "sortField": "id",
                "sortOrder": "asc",
                "ids": [
                    0
                ],
                "id": 0
            }).then((res) => {
                setRssEvent(res.data?.data);
            });
            await getData(pointsU.getSosyal).then((res) => {
                setSosyal(res.data);
                dispatch(setSosyalAction(res.data))
            });
            await postData(pointsU.GetMostReadedEvents ,{
                "categoryId": parseInt(params?.id) ?? 0,
                "pageSize": 5,
                "currentPage": 1,
                "search": "",
                "sortField": "id",
                "sortOrder": "asc",
                "ids": [
                    0
                ],
                "id": 0
            }).then((res) => {
                setEnFazlaOkuyanlar(res.data?.data);
            });

        }
        getData();
        getData2();
        getData3();
    }, []);

    useEffect(() => {
        setInterval(() => {
                setCount(count+1);
        }, 5000);
        // return () => clearInterval(interval);
    }, []);

    return (
        <Col sm={4}>
            <div className="left-content social-icons">
                <h1 class="heading_1"><span>صفحاتنا على الشبكات الاجتماعية</span></h1>
                <div>
                    <Row>
                        {
                            sosyal?.map((n, i) => {
                                return (
                                    <Col sm="4">
                                        <div className={n?.name}> <a href={n?.url ?? "#"}>أشترك لدينا
                                            {
                                                MediaEnums?.map(m => {
                                                    if (n?.name === m?.value) {
                                                        return (
                                                            m.icon
                                                        )
                                                    }
                                                })
                                            }
                                        </a> </div>
                                    </Col>
                                )
                            })
                        }
                        {/* <Col >
                            <div className="facebook"> متابعين 3452<FaGooglePlus /> </div>
                        </Col>
                        <Col >
                            <div className="twitter">متابعين 3452 <FaTwitterSquare /></div>
                        </Col>
                        <Col >
                            <div className="twitter">متابعين 3452 <FaGooglePlus /></div>
                        </Col> */}
                    </Row>
                    {/* <Row>
                        <Col >
                            <div className="youtube">متابعين 3452 <FaYoutube /></div>
                        </Col>
                        <Col >
                            <div className="instagram">متابعين 3452 <FaGooglePlus /></div>
                        </Col>
                        <Col >
                            <div className="behance">متابعين 3452 <FaBehanceSquare /></div>
                        </Col>
                    </Row> */}
                </div>
            </div>
            <div className="left-content">
                <h1 class="heading_1"><span>أخبار أكثر</span></h1>
                {
                    rssEvent?.map((b, i) => {
                        return (
                            <div key={i}>
                                <Row>
                                    <Col md={9}>
                                        <a href={b?.link}>
                                            <h5>{b?.title}</h5>
                                            <b style={{ display: "flex" }}>{b?.publishDate}  <FaCalendar style={{
                                                "width": "11px",
                                                "margin-right": "2px",
                                                "margin-top": "4px"
                                            }} /></b>
                                            {b?.description}
                                        </a>
                                    </Col>
                                    <Col md={3}>
                                        <img src={b?.photo64 ? "data:image/jpeg;base64," + b?.photo64 : "/assets/images/person_1.jpg"} height="60px" width="100%" alt="test img" />
                                    </Col>
                                </Row>
                            </div>
                        )
                    }
                    )
                }
            </div>
            <div>
                <a href={adsStore[count]?.url}><img src={adsStore[count]?.url ? "data:image/jpeg;base64," + adsStore[count]?.photo64 : "/assets/images/ads.jpg"} style={{ width: "90%", height: "70px", margin: "20px" }} alt={adsStore[count]?.name} /></a>
            </div>
            <div className="left-content">
                <h1 class="heading_1"><span>الأكثر قراءة</span></h1>
                {
                    enFazlaOkuyanlar?.map((b, i) => {
                        return (
                            <div key={i}>
                                <Row>
                                    <Col md={9}>
                                        <a href="#">
                                            <h5>{b?.title}</h5>
                                            {b?.description}
                                        </a>
                                    </Col>
                                    <Col md={3}>
                                        <img src={b?.photo64 ? "data:image/jpeg;base64," + b?.photo64 : "/assets/images/person_1.jpg"} height="60px" width="100%" alt="test img" />
                                    </Col>
                                </Row>
                            </div>
                        )
                    }
                    )
                }
            </div>
        </Col>
    )
}
