import { useEffect, useState } from 'react';
import CardCustum from "../../../elements/card/Card";
import { Form, Select, Button, DatePicker } from 'antd';
import { InboxOutlined, UploadOutlined } from '@ant-design/icons';
import InputCustum from '../../../elements/input/Input';
import TextArea from 'antd/lib/input/TextArea';
import appServices from '../../../services/appServices';
import { useHistory } from 'react-router';
import getBase64 from '../../../lib/helpers/getBase64';
import UploadCustum from "../../../elements/upload/Upload";
import moment from "moment";
pointsU
import SelectCustum from '../../../elements/select/Select';
import DebounceSelect from '../../../elements/select/Select';
import _app from '../../../services/_app';
import Editor from '../../../elements/editor/Editor';
import pointsU from 'points';


const { Option } = Select;
const formItemLayout = {
    labelCol: {
        span: 6,
    },
    wrapperCol: {
        span: 14,
    },
};



const IcerikEkleDuznle = () => {
    const [form] = Form.useForm();
    const [path, setPath] = useState(window.location.pathname);
    const [contentFoto, setContentFoto] = useState(null);
    const [editor, setEditor] = useState("");
    const [photoReloaded, setPhotoReloaded] = useState(false);
    const history = useHistory();


    const onFinish = async (values) => {
        try {
            var obj = contentFoto?.length ? contentFoto[0] : "";
            let x = "";
            if (obj) {
                if (obj.originFileObj) {
                    x = await getBase64(obj.originFileObj);
                } else {
                    x = obj.url;
                }
            }
            values.body = editor ?? "";
            values.categoryId = parseInt(_app.getSessionItem("kategoriId"));
            values.photo64 = x.substr(x.indexOf(",") + 1, x.length);
            values.imageName = obj?.name;
            values.photoReloaded = photoReloaded;
            delete values.upload;
            // is Düzenle
            if (!path.toString().includes("create")) {
                values.id = parseInt(path.substring(path.lastIndexOf("/") + 1)) ?? null;
                await appServices.postRequests(values, pointsU.addEvent);
            } else {
                await appServices.postRequests(values, pointsU.addEvent);
            }
            history.push("/Admin/categories/details/"+values.categoryId);
        } catch (err) {
            console.error("err");
            console.error(err);
        }
    };

    useEffect(() => {
        if (!path.includes("create")) {
            appServices.getRequests(pointsU.getEvent + "?eventId=" + path.substr(path.lastIndexOf("/") + 1)).then((res) => {
                form.setFieldsValue({ ...res.data, date: moment(res.data?.date) });
                setEditor(res.data.body);
                setContentFoto([
                    {
                        uid: '-1',
                        name: res.data?.photoUrl,
                        status: 'done',
                        url: res.data?.photo64 ? "data:image/png;base64,"+res.data?.photo64 : "",
                    }
                ])
            })
        }
    }, []);

    const onChange = async ({ fileList: newFileList }) => {
        let f = [];
        setPhotoReloaded(true);
        console.log(newFileList);

        for (let i = 0; i < newFileList.length; i++) {
            var obj = newFileList[i];
            if (obj["status"]) {
                obj["status"] = "done";
            }
            if (obj["originFileObj"]) {
                let x = await getBase64(obj.originFileObj);
                let b = x.substr(x.indexOf(",") + 1, x.length);
                // on submit array
                f.push({
                    name: obj.name,
                    type: obj.type,
                    fileBase64: b,
                });
            } else {
                let b = obj?.url?.substr(obj.url.indexOf(",") + 1, obj.url.length);
                f.push({
                    name: obj.name,
                    type: "",
                    fileBase64: b,
                });
            }
        }

        setContentFoto(newFileList);
    };

    const titleInput = (<InputCustum placeholder="العنوان" />);


    return (
        <CardCustum title={" أضف محتوى جديد : " + "(" +  _app.getSessionItem("kategori") + ")"}>
            <Form
                form={form}
                name="validate_other"
                {...formItemLayout}
                onFinish={onFinish}
            >
                <Form.Item
                    name="title"
                    label="العنوان"
                >
                    {titleInput}
                </Form.Item>
                <Form.Item
                    name="description"
                    label="الوصف"
                    hasFeedback
                >
                    <TextArea placeholder='الوصف'></TextArea>
                </Form.Item>

                <Form.Item name="date" label="التاريخ"  >
                    <DatePicker
                        style={{
                            width: '100%',
                        }}
                    />
                </Form.Item>

                <Form.Item
                    name="published"
                    label="الحالة"
                >
                    <Select placeholder="يرجى الأختيار">
                        <Option value={true}>نشر</Option>
                        <Option value={false}>عدم النشر</Option>
                    </Select>
                </Form.Item>

                <Form.Item
                    name="upload"
                    label="تحميل صورة"
                >
                    <UploadCustum
                        single={true}
                        listType="picture"
                        fileList={contentFoto}
                        onChange={onChange}
                    >
                        <p className="ant-upload-drag-icon">
                            <InboxOutlined />
                        </p>
                        <p className="ant-upload-text">
                            Click or drag file to this area to upload
                        </p>
                        <p className="ant-upload-hint">
                            Support for a single or bulk upload. Strictly prohibit from uploading
                            company data or other band files
                        </p>
                    </UploadCustum>
                </Form.Item>

                <Editor value={editor} onChange={(e)=> setEditor(e)}/>

                <Form.Item
                    wrapperCol={{
                        span: 12,
                        offset: 6,
                    }}
                >
                    <Button type="primary" htmlType="submit">
                        أرسال
                    </Button>
                </Form.Item>
            </Form>
        </CardCustum>);
};


export default IcerikEkleDuznle;
