import { useEffect, useState } from "react";
import { Col, Row } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { useRouter } from "next/router";
import MediaEnums from "../enums/MediaEnums";
import pointsU from "points";
import { FaCalendar } from "react-icons/fa";
import { setSosyalAction } from "redux/app/actions";
import { postData  , getData} from "utils/fetchData";

export default function LeftSidebar() {

    const [sosyal, setSosyal] = useState();
    const [enFazlaOkuyanlar, setEnFazlaOkuyanlar] = useState();
    const [rssEvent, setRssEvent] = useState();
    const dispatch = useDispatch();
    const params = useRouter();
    const adsStore = useSelector(state => state.App?.ads?.leftSidebar ?? []);
    const [count, setCount] = useState(0);
    const bb = adsStore?.length;

    useEffect(() => {
        const getData1 = async () => {
            var res = await postData(pointsU.getRssEvents,{
                "categoryId": parseInt(params?.id) ?? 0,
                "pageSize": 5,
                "currentPage": 1,
                "search": "",
                "sortField": "id",
                "sortOrder": "asc",
                "ids": [
                    0
                ],
                "id": 0
            });
            setRssEvent(res.data);
        }
        const getData2 = async () => {
            var res = await getData(pointsU.getSosyal);
            console.info("1111" , res);
            setSosyal(res);
            dispatch(setSosyalAction(res));
        }
        const getData3 = async () => {
            var res = await postData(pointsU.GetMostReadedEvents ,{
                "categoryId": parseInt(params?.id) ?? 0,
                "pageSize": 5,
                "currentPage": 1,
                "search": "",
                "sortField": "id",
                "sortOrder": "asc",
                "ids": [
                    0
                ],
                "id": 0
            });
            setEnFazlaOkuyanlar(res.data);

        }
        getData2();
        getData1();
        getData3();
    }, []);

    useEffect(() => {
        setInterval(() => {
                setCount(count+1);
        }, 5000);
        // return () => clearInterval(interval);
    }, []);

    return (
        <div className="left-content">
        <h1 class="heading_1"><span>صفحاتنا على الشبكات الاجتماعية</span></h1>
        <div>
            <Row>
                {
                    sosyal?.map((n, i) => {
                        return (
                            <Col sm="4">
                                <div className={n?.name}> <a href={n?.url ?? "#"}>أشترك لدينا
                                    {
                                        MediaEnums?.map(m => {
                                            if (n?.name === m?.value) {
                                                return (
                                                    m.icon
                                                )
                                            }
                                        })
                                    }
                                </a> </div>
                            </Col>
                        )
                    })
                }
                {/* <Col >
                    <div className="facebook"> متابعين 3452<FaGooglePlus /> </div>
                </Col>
                <Col >
                    <div className="twitter">متابعين 3452 <FaTwitterSquare /></div>
                </Col>
                <Col >
                    <div className="twitter">متابعين 3452 <FaGooglePlus /></div>
                </Col> */}
            </Row>
            {/* <Row>
                <Col >
                    <div className="youtube">متابعين 3452 <FaYoutube /></div>
                </Col>
                <Col >
                    <div className="instagram">متابعين 3452 <FaGooglePlus /></div>
                </Col>
                <Col >
                    <div className="behance">متابعين 3452 <FaBehanceSquare /></div>
                </Col>
            </Row> */}
        </div>
    </div>
    )
}
