import React from 'react'
import { Col, Container, Row } from 'react-bootstrap'
import { useState } from "react";
import Slider from "react-slick";
import { FaCalendar } from "react-icons/fa";
import { useRouter } from "next/router";
import convertDate from "utils/moment";

export default function IcerIkDetay() {
    const params = useRouter();
    const path = params.query.category;
    const [state, setState] = useState({
        display: true,
        width: 200
    });
    const [title, setTitle] = useState();
    const [rssEvent, setRssEvent] = useState();
    const [categoryName, setCategoryName] = useState("");
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1
    };

    return (
        <Container>
            <Row>
                <Col>          
                <div
                    style={{
                        display: state.display ? "block" : "none"
                    }}
                >
                    <Slider {...settings}>
                        <div className="slide-content">
                            <img width="100%" height="200px" src="/assets/images/indir.png" />
                        </div>
                    </Slider>
                </div></Col>
                <Col>2</Col>
            </Row>
        </Container>
    )
}
