import { Col, Container, Row } from 'react-bootstrap'
import { useState } from "react";
import Slider from "react-slick";
import { useRouter } from "next/router";

class CenterMode extends Component {
    render() {
      const settings = {
        customPaging: function(i) {
          return (
            <a>
              <img src={`/assets/images/indir${i + 1}.jfif`} />
            </a>
          );
        },
        dots: true,
        dotsClass: "slick-dots slick-thumb",
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1
      };
      return (
        <div>
          <h2>Custom Paging</h2>
          <Slider {...settings}>
            <div>
            <img src={`/assets/images/indir${i + 1}.jfif`} />
            </div>
            <div>
              <img src={baseUrl + "/abstract02.jpg"} />
            </div>
            <div>
              <img src={baseUrl + "/abstract03.jpg"} />
            </div>
            <div>
              <img src={baseUrl + "/abstract04.jpg"} />
            </div>
          </Slider>
        </div>
      );
    }
}

export default function IcerIkDetay() {
    const params = useRouter();
    const [state, setState] = useState({
        display: true,
        width: 200
    });
    const settings = {
        customPaging: function(i) {
            console.info("iiii");
            console.info(i);
            return (
              <a>
                <img src={`/assets/images/indir${i + 1}.jfif`} />
              </a>
            );
        },
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 2
    };

    return (
        <Container>
            <Row>
                <Col>          
                <div
                    style={{
                        display: state.display ? "block" : "none"
                    }}
                >
                    <Slider {...settings}>
                        <div className="slide-content">
                            <img width="100%" height="200px" src="/assets/images/indir1.jfif" />
                        </div>
                        <div className="slide-content">
                            <img width="100%" height="200px" src="/assets/images/indir2.jfif" />
                        </div>
                        <div className="slide-content">
                            <img width="100%" height="200px" src="/assets/images/indir3.jfif" />
                        </div>
                        <div className="slide-content">
                            <img width="100%" height="200px" src="/assets/images/indir2.jfif" />
                        </div>
                    </Slider>
                </div></Col>
                <Col>2</Col>
            </Row>
        </Container>
    )
}
