import { Col, Container, Row } from "react-bootstrap";
import LeftSidebar from "../../../Home/leftSidebar";
import { useEffect, useState } from "react";
import appServices from "../../../../services/appServices";
import ReactHtmlParser from 'react-html-parser';
import pointsU from "points";

export default function IcerıkDetay({params}) {
    const [data , setData] = useState();

    useEffect(()=>{
        console.info("params___!__içerik_detay");
        console.info(params);
        appServices.getRequests(pointsU.sgetIcerik + "?eventId=" + params?.id).then((res)=>{
            setData(res.data);
        });
    },[params]);

    return (
        <Container className="mt-100">
            <Row>
                <Col sm={8}>
                    <div id='detay'>
                        {
                            data?.photo64 ? (
                                <img src={data.photo64} alt={data?.title} />
                            ) : ""
                        }
                        <h2>{data?.title}</h2>
                        {ReactHtmlParser(data?.body)}
                    </div>
                </Col>
                <LeftSidebar />
            </Row>
        </Container>
    )
}
