import 'antd/dist/antd.css';
import '../styles/globals.scss';
import LayoutAdmin from '../layout/admin/index';
import { Provider } from "react-redux";
import { store } from "../redux/store";
import { ConfigProvider } from 'antd';
import { useRouter } from 'next/router';
import LayoutWeb from '../layout/web';
import { isAuthorization } from 'utils/services';

const admin = ["/admin"];
const notLayout = ["/login"];

const ChangeLayout = ({children})=>{
  const router = useRouter();
  if(router.pathname.includes(admin)){
    let isAuthed = isAuthorization();
    if(!isAuthed){
      router.push("/login");
    }
    return (
      <LayoutAdmin>
        {children}
      </LayoutAdmin>
    )
  }else if(router.pathname.includes(notLayout)){
    return (
      <>
        {children}
      </>
    )
  }else{
    return (
      <LayoutWeb>
        {children}
      </LayoutWeb>
    )
  }
}

function MyApp({ Component, pageProps }) {
  return (
    <Provider store={store}>
      <ConfigProvider dir="rtl">
        <ChangeLayout>
          <Component {...pageProps} />
        </ChangeLayout>
      </ConfigProvider>
    </Provider>
  )
}

export default MyApp
