import { Button, Dropdown, Menu } from "antd";
import React, { useEffect } from "react";
import { AiOutlineEllipsis } from "react-icons/ai";
import ButtonCustum from "../elements/button/Button";
import { NavLink } from "react-router-dom";
import { reloadDatatable, setKategoriId } from "../../redux/app/actions";
import { useDispatch } from 'react-redux';
import Swal from "sweetalert2";
import ErrorProvider from "../../services/hataYoetimi";
import appServices from "../../services/appServices";
import { DeleteOutlined, EditOutlined, SearchOutlined } from "@ant-design/icons";
import { deleteData, postData } from "utils/fetchData";

const Tablemenuitems = (props) => {

  const dispatch = useDispatch()
  var { detay } = props;
  const deleteItem = () => {
    try {
      Swal.fire({
        title: "هل أنت متأكد أنك تريد الحذف ?",
        icon: "warning",
        // text: "devam etmek istiyor musunuz?",
        showConfirmButton: true,
        showCancelButton: true,
        cancelButtonText: "لا",
        confirmButtonText: "نعم",
      }).then(async confirm => {
        if (confirm.isConfirmed) {
          var t = await postData({
            ids: [props.sil?.id],
            currentPage: 1,
            pageSize: 10,
            search: "",
            sortField: "descend",
            sortOrder: "id"
          }, props.sil?.url);
          dispatch(reloadDatatable());
          Swal.fire({
            title: "تم الحذف",
            icon: "success",
            showCancelButton: true,
            showConfirmButton: false,
            cancelButtonText: "تمام",
          })
        }
      });
    } catch (err) {
      ErrorProvider(err);
    }
  }



  var Main = (
    <>
          {props?.detay ? (
        <ButtonCustum onClick={() => {
          // console.info(detay?.subStr(detay.lastIndexOf("/")+1))
          dispatch(setKategoriId(props.sil?.id));
          window.sessionStorage.setItem("kategoriId", props.sil?.id);
          window.sessionStorage.setItem("kategori", props.sil?.text);
        }}>
          <NavLink to={props.detay}><SearchOutlined/></NavLink>
        </ButtonCustum>
      ) : ""}
      {
        props.duzenle ? (
          <ButtonCustum>
            <NavLink to={props.duzenle ?? "notfound"}><EditOutlined /></NavLink>
          </ButtonCustum>
        ) : ""
      }
      {props?.sil ? (
        <ButtonCustum disabled={props?.sil?.disabled ? true : false}>
          <NavLink onClick={deleteItem}  to="#"><DeleteOutlined /></NavLink>
        </ButtonCustum>
      ) : ""}

    </>
  )

  return Main;

  return (
    <span>
      <Dropdown
        overlay={
          <Menu>
            {props.duzenle ? (
              <Menu.Item>
                <NavLink to={props.duzenle}>تعديل</NavLink>
              </Menu.Item>
            ) : ""}
            {props.sil ? (
              <Menu.Item>
                <NavLink onClick={deleteItem} to="#">مسح</NavLink>
              </Menu.Item>
            ) : ""}
            {
              props.digerComponentContent ? (
                props.digerComponentContent
              ) : ""
            }
            {props.detay ? (
              <Menu.Item onClick={() => {
                // console.info(detay?.subStr(detay.lastIndexOf("/")+1))
                dispatch(setKategoriId(props.sil?.id));
                window.sessionStorage.setItem("kategoriId", props.sil?.id);
              }}>
                <NavLink to={props.detay}>التفاصيل</NavLink>
              </Menu.Item>
            ) : ""}

          </Menu>
        }
        placement="bottomLeft"
        arrow
      >
        <ButtonCustum type="default">
          <AiOutlineEllipsis />
        </ButtonCustum>
      </Dropdown>
    </span>
  );
};


export default Tablemenuitems;
