import { useState } from 'react';
import Link from "next/router";
import { useRouter } from 'next/router';
import { FaCalendar, FaShare } from "react-icons/fa";

export default function CardLg({data}) {

    const history = useRouter();
    const [color, setColor] = useState(["red", "green", "yellow", "blue"]);

    // const getIcerikler = () => {
    //     // axios.get(config.apiUrl + pointsU.sgetCardContent + "?categoryId=" + 2).then(res => {
    //     //     console.info(res.data)
    //     //     setData(res.data);
    //     // })
    // }

    // useEffect(() => {
    //     getIcerikler();
    // }, [active])

 



    const IfNotData = () => {
        if (data?.length < 0) return;
        return (
            <>
                <section>
                    <div className="container">
                        {/* <div className="h1 text-center text-dark" id="pageHeaderTitle">My Cards Light</div> */}
                        <article className={"postcard " + "color[i]"}>
                            <Link className="postcard__img_link" to={"/icerik/" + "v?.id" || "/icerik/0"}>
                                <img className="postcard__img" src="data:image/jpeg;base64," alt="Image Title" />
                            </Link>
                            <div className="postcard__text t-dark">
                                <h1 className="postcard__title blue"><Link to={"/icerik/" + "v?.id" || "/icerik/0"}>مقتل مسن على يد شاب داخل مسجد وسط مدينة ذمار</Link></h1>
                                <div className="postcard__subtitle small">
                                    <time datetime="2020-05-25 12:00:00">
                                        20.20.2021  <FaCalendar />
                                    </time>
                                </div>
                                <div className="postcard__bar"></div>
                                <div className="postcard__preview-txt"> وأكدت المصادر أن القيادي الحوثي "عبدالملك حنتوس" المعين من قبل المليشيات مديراً للتم مبنى مركز تحفيظ ب السلفية ومن ثم عبثوا بمحتوياته وإشعال النيران بداخله واغلاقه".</div>
                                <ul className="postcard__tagbox">
                                    <li className="tag__item"><FaShare /></li>
                                    <li className="tag__item readMore">متابعة القراءة</li>
                                    {/* <li className="tag__item"><i className="fas fa-clock mr-2"></i>55 mins.</li>
                                <li className="tag__item play blue">
                                    <a href="#"><i className="fas fa-play mr-2"></i>Play Episode</a>
                                </li> */}
                                </ul>
                            </div>
                        </article>
                    </div>
                </section>
            </>
        )
    }


    return (
        <div id='sectionLg'>
            {
                data?.map((v, i) => {
                    if (i > 4) return "";
                    return (
                        <section key={"cardlg"+i}>
                            <div className="container">
                                {/* <div className="h1 text-center text-dark" id="pageHeaderTitle">My Cards Light</div> */}
                                <article className={"postcard " + color[i]}>
                                    <Link className="postcard__img_link" to={"/news/" + v?.id || "/news/0"}>
                                        <img className="postcard__img" src={v?.photo64 ? "data:image/jpeg;base64,"+v?.photo64 : "/assets/images/default.png"} alt="Image Title" />
                                    </Link>
                                    <div className="postcard__text t-dark">
                                        <h1 className="postcard__title blue"><Link to={"/news/" + v?.id || "/news/0"}> {v?.title}</Link></h1>
                                        <div className="postcard__subtitle small">
                                            <time datetime="2020-05-25 12:00:00">
                                                {v?.date}  <FaCalendar />
                                            </time>
                                        </div>
                                        <div className="postcard__bar"></div>
                                        <div className="postcard__preview-txt"> {v?.description}</div>
                                        <ul className="postcard__tagbox">
                                            <li className="tag__item"><FaShare /></li>
                                            <li className="tag__item readMore">متابعة القراءة</li>
                                            {/* <li className="tag__item"><i className="fas fa-clock mr-2"></i>55 mins.</li>
                                          <li className="tag__item play blue">
                                              <a href="#"><i className="fas fa-play mr-2"></i>Play Episode</a>
                                          </li> */}
                                        </ul>
                                    </div>
                                </article>
                            </div>
                        </section>
                    )
                }
                )
            }

            {/* <IfNotData/> */}
          
        </div>
    )
}
