import { useEffect, useState } from "react";
import { Container } from "react-bootstrap";
import Slider from "react-slick";
import { FaCalendar } from "react-icons/fa";
import pointsU from "points";
import { postData } from "utils/fetchData";
import { useRouter } from "next/router";
import convertDate from "utils/moment";
import { Rate } from "antd";

const Product = ({products}) => {
    const params = useRouter();
    const path = params.query.category;
    const [state, setState] = useState({
        display: true,
        width: 200
    });
    const [title, setTitle] = useState();
    const [rssEvent, setRssEvent] = useState();
    const [categoryName, setCategoryName] = useState("");

    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 1
    };

    return (
        <Container className="slide">
            <div>
                <h1 class="heading_1"><span>{categoryName ?? title}</span></h1>
                <Slider {...settings}>
                    {/*
                        "id": 5,
                        "title": "product2",
                        "published": false,
                        "description" : "en iyi canallardas ksk asjfas safsaf",
                        "price" : "32,12",
                        "discount" : "433,12",
                        "rate" : 1,
                        "sorting": 0,
                        "views": 0,
                    */}
                    {
                        products?.map((b, i) => {
                            return (
                                <div className="slide-content">
                                    <img width="100%" height="200px" src={b?.photo ? b.photo : "/assets/images/default.png"} />
                                    <div className="body">
                                        <b className="rate-slide"><Rate defaultValue={b.rate}/></b>
                                        <div className="title">{b?.title}</div>
                                        <div className="description">
                                            {b?.description}...
                                        </div>
                                        <div className="price">
                                            {b?.discount} <del>{b?.price}</del>
                                        </div>
                                        <div className="price">
                                            {b?.discount} <del>{b?.price}</del>
                                        </div>
                                    </div>
                                </div>
                            )
                        }
                        )
                    }
                </Slider>
            </div>
        </Container>
    );

}

export default Product;
