import React, { Component, useEffect, useState } from "react";
import { Container } from "react-bootstrap";
import Slider from "react-slick";
import { FaCalendar } from "react-icons/fa";
import "./slide.style.scss";
import appServices from "../../services/appServices";
import { useParams } from "react-router";
import pointsU from "../../config/points";
import defaultImg from "../../assets/images/default.png";

const SliderCustum = () => {
    const params = useParams();
    const [state, setState] = useState({
        display: true,
        width: 200
    });
    const [title, setTitle] = useState();
    const [rssEvent, setRssEvent] = useState();

    useEffect(() => {
        appServices.postRequests({
            "categoryId": parseInt(params?.id) ?? 0,
            "pageSize": 5,
            "currentPage": 1,
            "search": "",
            "sortField": "id",
            "sortOrder": "asc",
            "ids": [
                0
            ],
            "id": 0
        }, pointsU.getRssEvents).then((res) => {
            setRssEvent(res.data?.data);
            setTitle(res?.data?.title);
        });
    }, [])



    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 1
    };

    return (
        <Container className="slide">
            <div
                style={{
                    display: state.display ? "block" : "none"
                }}
            >
                <h1 class="heading_1"><span>{params?.category ?? title ?? ""}</span></h1>
                <Slider {...settings}>
                    {
                        rssEvent?.map((b, i) => {
                            return (
                                <div className="slide-content" onClick={()=> {window.location.href = b?.link}}>
                                    <img width="100%" height="200px" src={b?.photo ? "data:image/jpeg;base64," + b.photo : defaultImg} />
                                    <b>{b?.title}</b>
                                    <div style={{ display: "flex" }}>    {b?.publishDate}  <FaCalendar style={{
                                        "width": "11px",
                                        "margin-left": "5px",
                                        "margin-top": "4px"
                                    }} />
                                    </div>
                                    <div>
                                        {b?.description}
                                    </div>
                                </div>
                            )
                        }
                        )
                    }
                </Slider>
            </div>
        </Container>
    );

}

export default SliderCustum;
