import columnsKategori from "../../../columns/kategori";
import CardCustum from "../../../components/elements/card/Card";
import dynamic from "next/dynamic";
import ButtonCustum from "components/elements/button/Button";
import { useState } from "react";
import ModalCustum from "components/elements/modal/Modal";
import { Button, Form, Select } from "antd";
import InputCustum from "components/elements/input/Input";
import { postData } from "utils/fetchData";

const TableCustum = dynamic(() => import("../../../components/elements/table/Table"), {
  // Do not import in server side
  ssr: false,
});

const layout = {
  labelCol: {
    span: 8,
  },
  wrapperCol: {
    span: 16,
  },
};

const kategori = () => {
  const [visible,setVisible] = useState();
  const [id, setId] = useState(0);
  const [form] = Form.useForm();
  const dispatch = useDispatch();
  const changeVisible = () => {
    setVisible(false)
    form.setFieldsValue({
      name: "",
      published: false
    })
    setId(0)
  }
  const onFinish = async (values) => {
    try {
      values.id = id;
      await postData("/Home/AddUpdateCategory", {...values});
      setVisible(false);
      dispatch(reloadDatatable(Math.random()));
    } catch (err) {
      // ErrorProvider(err);
    }
  }
  return (
    <div>
      <CardCustum extra={
        <>
          <ButtonCustum onClick={() => {
            setVisible(true);
            form.setFieldsValue({
              name: ""
            })
          }} type="primary">
            أضف جديد
          </ButtonCustum>
        </>
      } title=" الأقسام">
        <TableCustum url="/Home/GetEvents" columns={columnsKategori}/>
      </CardCustum>
      <ModalCustum footer="" title="أضف قسم جديد" setVisible={changeVisible} visible={visible}>
        <Form form={form} {...layout} name="nest-messages"  onFinish={onFinish}>
          <Form.Item
            name="name"
            label="الأسم"
          >
            <InputCustum placeholder="أدخل أسم القسم" />
          </Form.Item>
          <Form.Item
            name="published"
            label="الحالة"
          >
            <Select placeholder="يرجى الأختيار">
              <Select.Option value={true}>نشر</Select.Option>
              <Select.Option value={false}>عدم النشر</Select.Option>
            </Select>
          </Form.Item>
          <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
            <Button type="primary" htmlType="submit">
              أرسال
            </Button>
          </Form.Item>
        </Form>
      </ModalCustum>
    </div>
  )
}

export default kategori;
