import { authService } from '../../services/authServices';
import authConfig from '../../config/siteConfig';
import { ErrorProviderCallout } from '../../services/hataYoetimi';
 
export const CHECK_AUTHORIZATION    = 'CHECK_AUTHORIZATION';
export const LOGIN_SUCCESS          = 'LOGIN_SUCCESS';
export const LOGIN_ERROR            = "LOGIN_ERROR";
export const CHECK_LOGIN            = "CHECK_LOGIN";
export const LOGOUT                 = "LOGOUT";

const loginSuccess = user => {
    return {
        type: LOGIN_SUCCESS,
        user
    };
};

const loginError = error => {
    return {
        type: LOGIN_ERROR,
        error
    };
};

export const checkAuthorization = () => {
    const token = localStorage.getItem(authConfig.tokenLabel);
    if (token) {
        //is exp?
        return dispatch => {
            dispatch(loginSuccess(token))
        }
    }
    return dispatch => {
        dispatch(loginError(""))
    }
}

export const login = (username, password) => {
    return dispatch => {
        authService.login(username, password)
            .then(result => {
                let data = result.data;
                // console.error(result);
                // console.error(data);
               
                if(data.status === false){
                    console.error("false geldi");
                   
                    // dispatch(loginSuccess(data));
                    dispatch(loginError(data.error));
                    return;
                }
                window.location.assign("/Admin");
            })
            .catch(err => {
                ErrorProviderCallout(err)
                // let status = err.response?.data ;
                // console.log(err.response.data.description)
                // let message = "";
                // status === 401
                //     ?  message = status
                //     :  message = err.response.data.description
                // dispatch(loginError(message));
            });
    }
}

export const logout = () => {
    authService.logOut();
    return {
        type: LOGOUT,
    };
}
