import 'antd/dist/antd.css';
import '../styles/globals.css';
import LayoutAdmin from '../layout/admin/index';
import { Provider } from "react-redux";
import { store } from "../redux/store";
import { ConfigProvider } from 'antd';
import { useRouter } from 'next/router';
import LayoutWeb from '../layout/web';

const admin = "/admin";
const notLayout = ["/login"];

const ChangeLayout = ({children})=>{
  const router = useRouter();
  console.info("router");
  console.info(admin.includes(router.pathname));
  if(admin.includes(router.pathname)){
    return (
      <LayoutAdmin>
        {children}
      </LayoutAdmin>
    )
  }else if(notLayout.includes(router.pathname)){
    return (
      <>
        {children}
      </>
    )
  }else{
    return (
      <LayoutWeb>
        {children}
      </LayoutWeb>
    )
  }
}

function MyApp({ Component, pageProps }) {
  return (
    <Provider store={store}>
      <ConfigProvider dir="rtl">
        <ChangeLayout>
          <Component {...pageProps} />
        </ChangeLayout>
      </ConfigProvider>
    </Provider>
  )
}

export default MyApp
