import { Button, Col, Row } from "react-bootstrap";
import styles from "./contact.module.scss";
import { Form, Select } from "antd";
import InputCustum from "components/elements/input/Input";
import { postData } from "utils/fetchData";


export default function Contact() {
    const [form] = Form.useForm();
    const onFinish = async (values) => {
        try {
          values.id = id;
          await postData("/Home/AddUpdateCategory", {...values} , true);
        //   setVisible(false);
        //   dispatch(reloadDatatable(Math.random()));
        } catch (err) {
          // ErrorProvider(err);
        }
      }
      const layout = {
        labelCol: {
          span: 8,
        },
        wrapperCol: {
          span: 16,
        },
      };
 
    return (
        <div className={styles.contact}>
            <Row>
                <Col>
                    <div className={styles.textContent}>
                        <div className="heading_1"> <span>MODERN EQUIPMENT</span> </div>
                        <h3> Professionel washing and cleaning of you car </h3>
                        <p> For your car we will do everything â€“ advice, repairs and maintenance. We are the preferred choice by many car owners because our experience and knowledge is self-evident. </p>
                        <h5> Call for book : <span className={styles.number}>0538 201 50 72</span></h5>
                        <Button className={styles.readMore}>
                            READ MORE
                        </Button>
                    </div>
                </Col>
                <Col className="p-r-0">
                    <Form form={form} className={styles.form} name="nest-messages" onFinish={onFinish}>
                        <h3>Contact Us</h3>
                        <h3>Contact Us</h3>
                        <Form.Item
                            name="name"
                        >
                            <InputCustum placeholder="Name" />
                        </Form.Item>
                        <Form.Item
                            name="email"
                        >
                            <InputCustum placeholder="Email" />
                        </Form.Item>
                        <Form.Item
                            name="phone"
                        >
                            <InputCustum placeholder="phone" />
                        </Form.Item>
                        <Form.Item>
                            <Button type="primary" htmlType="submit">
