import 'antd/dist/antd.css';
import '../styles/globals.scss';
import LayoutAdmin from '../layout/admin/index';
import { Provider } from "react-redux";
import { store } from "../redux/store";
import { ConfigProvider } from 'antd';
import { useRouter } from 'next/router';
import LayoutWeb from '../layout/web';
import { isAuthorization } from 'utils/services';
import { useEffect, useState } from 'react';

const admin = ["/admin"];
const notLayout = ["/login"];

const ChangeLayout = ({children})=>{
  const router = useRouter();
  const [isLogiind , setIsLogiind] = useState(true);
  useEffect(()=>{
    console.info("3");
    let isAuthed = isAuthorization();
    console.info("isAuthed" , isAuthed);
    if(!isAuthed){
      router.push("/login");
    }
    setIsLogiind(false);
  })
  if(router.pathname.includes(admin)){
    if(isLogiind){
      return "";
    }
    return (
      <>
        {
        isLogiind ? (
          <LayoutAdmin>
            {children}
          </LayoutAdmin>
        ) : ""
      }
      </>
    )
  }else if(router.pathname.includes(notLayout)){
    return (
      <>
        {children}
      </>
    )
  }else{
    return (
      <LayoutWeb>
        {children}
      </LayoutWeb>
    )
  }
}

function MyApp({ Component, pageProps }) {
  return (
    <Provider store={store}>
      <ConfigProvider dir="rtl">
        <ChangeLayout>
          <Component {...pageProps} />
        </ChangeLayout>
      </ConfigProvider>
    </Provider>
  )
}

export default MyApp
